function getScrollbarWidth(){var e=document.createElement("div");e.style.visibility="hidden",e.style.width="50px",document.body.appendChild(e);var t=e.offsetWidth;e.style.overflow="scroll";var r=document.createElement("div");r.style.width="100%",e.appendChild(r);var o=r.offsetWidth;return e.parentNode.removeChild(e),t-o}function setScrollbarVar(e){document.body.style.setProperty("--scrollbarWidth",e)}function addScrollObserver(){new IntersectionObserver((e,t)=>{e.forEach(e=>{e.intersectionRatio<1?setScrollbarVar(getScrollbarWidth()+"px"):setScrollbarVar("0px")})},{threshold:1}).observe(document.body)}window.addEventListener("load",addScrollObserver);
