function getScrollbarWidth(){var outer=document.createElement("div");outer.style.visibility="hidden",outer.style.width="50px",document.body.appendChild(outer);var widthNoScroll=outer.offsetWidth;outer.style.overflow="scroll";var inner=document.createElement("div");inner.style.width="100%",outer.appendChild(inner);var widthWithScroll=inner.offsetWidth;return outer.parentNode.removeChild(outer),widthNoScroll-widthWithScroll}function setScrollbarVar(value){document.body.style.setProperty("--scrollbarWidth",value)}function addScrollObserver(){new IntersectionObserver((entries,observer)=>{entries.forEach(entry=>{entry.intersectionRatio<1?setScrollbarVar(getScrollbarWidth()+"px"):setScrollbarVar("0px")})},{threshold:1}).observe(document.body)}window.addEventListener("load",addScrollObserver);